mapper.select:
SELECT j.*,
       u.username AS username,
       a.username AS agentName,
       h.name AS houseName
FROM jump_order_report j
LEFT JOIN user u ON u.id = j.user_id
LEFT JOIN user a ON a.id = j.agent_id
LEFT JOIN house h ON h.id = j.house_id


        AND j.user_id = #{userId}


        AND j.agent_id = #{agentId}


        AND j.status = #{status}


ORDER BY j.create_time DESC

    LIMIT #{current},#{size}

SELECT COUNT(*)
FROM jump_order_report j


        AND j.user_id = #{userId}


        AND j.agent_id = #{agentId}


        AND j.status = #{status}
